local custom = {}

function tvRP.setTattoos(data)
    ClearPedDecorations(GetPlayerPed(-1))
    if data then
        custom = data
    end
end

function tvRP.addTattoo(tattoo,store,price)
    ClearPedDecorations(GetPlayerPed(-1))
    if tattoo and store then
        custom[tattoo] = {store,price}
    end
end

function tvRP.delTattoo(tattoo)
    ClearPedDecorations(GetPlayerPed(-1))
    if tattoo then
        custom[tattoo] = nil
    end
end

function tvRP.getTattoos()
    return custom
end


function tvRP.cleanPlayer()
    ClearPedDecorations(GetPlayerPed(-1))
    custom = {}
end

