            Citizen.Wait(1)
        end
        if custom then
            local ped = GetPlayerPed(-1)
            -- parts
            for k,v in pairs(custom) do
                ApplyPedOverlay(GetPlayerPed(-1), GetHashKey(v[1]), GetHashKey(k))
            end
        end
    end
end)